INSERT INTO `his_config_info` VALUES (0, 13, 'cloud-gateway-dev.yaml', 'DEFAULT_GROUP', '', 'server:\r\n  port: 9527\r\n\r\nspring:\r\n  application:\r\n    name: cloud-gateway\r\n  cloud:\r\n    nacos:\r\n      discovery:\r\n        server-addr: localhost:8848\r\n\r\n    gateway:\r\n      globalcors:\r\n        # gateway 跨域设置\r\n        cors-configurations:\r\n          \'[/**]\':\r\n            allowedOrigins: \"*\"\r\n            allowedHeaders: \"*\"\r\n            allowCredentials: true\r\n            allowedMethods:\r\n              - GET\r\n              - POST\r\n              - PUT\r\n              - DELETE\r\n              - OPTIONS\r\n      # 设置与服务注册发现组件结合，这样可以采用服务名的路由策略\r\n      discovery:\r\n        locator:\r\n          enabled: true\r\n      routes:\r\n        # 配置路由规则\r\n        - id: auth-center #payment_route    #路由的ID，没有固定规则但要求唯一，建议配合服务名\r\n          uri: lb://auth-center #匹配后提供服务的路由地址\r\n          predicates:\r\n            - Path=/auth/**         # 断言，路径相匹配的进行路由\r\n\r\n        - id: user-center\r\n          uri: lb://user-center\r\n          predicates:\r\n            - Path=/user/**\r\n\r\n        - id: blog-content\r\n          uri: lb://blog-content\r\n          predicates:\r\n            - Path=/content/**\r\n\r\n        - id: file-center\r\n          uri: lb://file-center\r\n          predicates:\r\n            - Path=/file/**\r\n\r\nlogging:\r\n  level:\r\n    com.alibaba.nacos.client.config.impl: WARN', '02cb16fe87837c589ef92945709f41b1', '2022-07-13 16:09:20', '2022-07-13 08:09:20', NULL, '0:0:0:0:0:0:0:1', 'I', 'bb537572-78ef-4d8d-a958-2db8757e96cc', '');
